INSERT INTO events (event_id, ds_prompt, is_card, prompt) VALUES (160, NULL, FALSE, 'Roll a D20 to attempt. If the attempt roll is more than 10, you grab the book without her noticing. If the book is about martial arts, search the Greater Loot pile for a card titled, "Xiaolin Martial Arts," and take it with you. If the book is about war, search the Greater Loot pile for a card titled, "Art of War," and take it with you. If the book is about magic, search the Greater Loot pile for a card titled, "Encyclopedia Magica," and take it with you. If the attempt roll is no greater than 10, the elf strikes you down. Roll a Savng Throw.');
