INSERT INTO events (event_id, ds_prompt, is_card, prompt) VALUES (182, NULL, TRUE, 'A man frantically emerges from a building. This makes no sense! It took 1 artifact worth 30 coins, then 2 artifacts worth 40 coins. It took 4 artifacts worth 60 coins from the last town! What does it want here?!');
