INSERT INTO events (event_id, ds_prompt, is_card, prompt) VALUES (185, NULL, FALSE, 'She smacks you in the face with her stick. Roll a D20 to stay conscious. If the roll to stay conscious is over 5, you manage to shake it off. If the roll to stay conscious is at most 5, roll a Saving Throw.');
