INSERT INTO events (event_id, ds_prompt, is_card, prompt) VALUES (221, NULL, FALSE, 'The 2 men have 16 hit-points combined. Roll 2 Saving Throws instead of 1 if they have more than 8 hit-points while in combat with them. Engage in combat with the 2 men. If you defeat the 2 men in combat, search the Greater Loot pile for 2 cards titled, "Soul Resonator," and take them with you.');
